import React, { useState } from "react";
import { saveAs } from "file-saver";
import * as XLSX from "xlsx";

// ðŸ”¼ Keep university data ABOVE the component
const universities = [
  {
    name: "Massachusetts Institute of Technology (MIT)",
    generalRank: 1,
    subjects: [
      "Computer Science",
      "Mathematics",
      "Engineering",
      "Physics",
      "Chemistry",
      "Biology",
      "Economics",
      "Architecture",
      "Linguistics",
      "Political Science",
      "Philosophy"
    ],
    fees: {
      international: 55000,
      domestic: 20000
    },
    intakes: ["January", "September"],
    applicationDeadlines: "March 15",
    eligibility: {
      international: "TOEFL >= 100, GPA >= 3.5, SAT >= 1400",
      domestic: "GPA >= 3.5, SAT >= 1400"
    },
    courseLinks: {
      "Computer Science": "https://www.mit.edu/computer-science",
      "Mathematics": "https://www.mit.edu/mathematics",
      "Engineering": "https://www.mit.edu/engineering",
      "Physics": "https://www.mit.edu/physics",
      "Chemistry": "https://www.mit.edu/chemistry",
      "Biology": "https://www.mit.edu/biology",
      "Economics": "https://www.mit.edu/economics",
      "Architecture": "https://www.mit.edu/architecture",
      "Linguistics": "https://www.mit.edu/linguistics",
      "Political Science": "https://www.mit.edu/political-science",
      "Philosophy": "https://www.mit.edu/philosophy"
    },
    subjectData: {
      "Computer Science": [
        { year: 2025, qsRank: 1, gpaCutoff: 3.8, satCutoff: 1500 },
        { year: 2024, qsRank: 1, gpaCutoff: 3.8, satCutoff: 1490 },
        { year: 2023, qsRank: 1, gpaCutoff: 3.7, satCutoff: 1480 },
        { year: 2022, qsRank: 1, gpaCutoff: 3.7, satCutoff: 1470 },
        { year: 2021, qsRank: 1, gpaCutoff: 3.6, satCutoff: 1460 }
      ],
      "Mathematics": [
        { year: 2025, qsRank: 2, gpaCutoff: 3.7, satCutoff: 1490 },
        { year: 2024, qsRank: 2, gpaCutoff: 3.7, satCutoff: 1480 },
        { year: 2023, qsRank: 2, gpaCutoff: 3.6, satCutoff: 1470 },
        { year: 2022, qsRank: 2, gpaCutoff: 3.6, satCutoff: 1460 },
        { year: 2021, qsRank: 2, gpaCutoff: 3.5, satCutoff: 1450 }
      ],
      "Engineering": [
        { year: 2025, qsRank: 1, gpaCutoff: 3.9, satCutoff: 1510 },
        { year: 2024, qsRank: 1, gpaCutoff: 3.9, satCutoff: 1500 },
        { year: 2023, qsRank: 1, gpaCutoff: 3.8, satCutoff: 1490 },
        { year: 2022, qsRank: 1, gpaCutoff: 3.8, satCutoff: 1480 },
        { year: 2021, qsRank: 1, gpaCutoff: 3.7, satCutoff: 1470 }
      ]
    }
  }
];

const App = () => {
  const [selectedUniversity, setSelectedUniversity] = useState(null);
  const [selectedSubject, setSelectedSubject] = useState("");
  const [studentType, setStudentType] = useState("international");
  const [gpa, setGpa] = useState("");
  const [sat, setSat] = useState("");

  const handleUniversityChange = (e) => {
    const uni = universities.find((u) => u.name === e.target.value);
    setSelectedUniversity(uni);
    setSelectedSubject(uni?.subjects[0] || "");
  };

  const handleConvertGpa = () => {
    if (!gpa) return "";
    const converted = (parseFloat(gpa) / 10) * 4;
    return converted.toFixed(2);
  };

  const getAdmissionProbability = () => {
    const g = parseFloat(gpa);
    const s = parseInt(sat);
    if (isNaN(g) || isNaN(s)) return "Enter GPA and SAT";
    if (g >= 3.8 && s >= 1500) return "High";
    if (g >= 3.5 && s >= 1400) return "Moderate";
    if (g >= 3.0 && s >= 1300) return "Low";
    return "Very Low";
  };

  const handleDownloadExcel = () => {
    if (!selectedUniversity || !selectedSubject) return;
    const data = selectedUniversity.subjectData[selectedSubject] || [];
    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Subject Rankings");
    const excelBuffer = XLSX.write(wb, { bookType: "xlsx", type: "array" });
    const blob = new Blob([excelBuffer], { type: "application/octet-stream" });
    saveAs(blob, `${selectedSubject}_QS_Data.xlsx`);
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>QS World University Rankings</h2>

      <label>Select University: </label>
      <select onChange={handleUniversityChange}>
        <option value="">Select</option>
        {universities.map((u, i) => (
          <option key={i} value={u.name}>{u.name}</option>
        ))}
      </select>

      {selectedUniversity && (
        <div>
          <p><strong>General QS Rank:</strong> {selectedUniversity.generalRank}</p>

          <label>Select Subject: </label>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
          >
            {selectedUniversity.subjects.map((subj, i) => (
              <option key={i}>{subj}</option>
            ))}
          </select>

          <label>Student Type: </label>
          <select
            value={studentType}
            onChange={(e) => setStudentType(e.target.value)}
          >
            <option value="international">International</option>
            <option value="domestic">Domestic</option>
          </select>

          <p><strong>Tuition Fees:</strong> ${selectedUniversity.fees[studentType]}</p>
          <p><strong>Intake Months:</strong> {selectedUniversity.intakes.join(", ")}</p>
          <p><strong>Application Deadline:</strong> {selectedUniversity.applicationDeadlines}</p>
          <p><strong>Eligibility:</strong> {selectedUniversity.eligibility[studentType]}</p>

          {selectedUniversity.courseLinks[selectedSubject] && (
            <p>
              <strong>Course Link:</strong>{" "}
              <a
                href={selectedUniversity.courseLinks[selectedSubject]}
                target="_blank"
                rel="noopener noreferrer"
              >
                Visit
              </a>
            </p>
          )}

          <h4>{selectedSubject} - QS Rankings (Last 5 Years)</h4>
          <table border="1" cellPadding="5">
            <thead>
              <tr>
                <th>Year</th>
                <th>QS Rank</th>
                <th>GPA Cutoff</th>
                <th>SAT Cutoff</th>
              </tr>
            </thead>
            <tbody>
              {(selectedUniversity.subjectData[selectedSubject] || []).map((d, i) => (
                <tr key={i}>
                  <td>{d.year}</td>
                  <td>{d.qsRank}</td>
                  <td>{d.gpaCutoff}</td>
                  <td>{d.satCutoff}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <br />
          <button onClick={handleDownloadExcel}>Download Excel</button>

          <h4>GPA to CGPA Converter</h4>
          <label>Enter GPA (out of 10): </label>
          <input
            type="number"
            value={gpa}
            onChange={(e) => setGpa(e.target.value)}
          />
          <p>Converted GPA (out of 4): <strong>{handleConvertGpa()}</strong></p>

          <h4>Student Inputs</h4>
          <label>GPA: </label>
          <input
            type="text"
            value={gpa}
            onChange={(e) => setGpa(e.target.value)}
          />
          <br />
          <label>SAT: </label>
          <input
            type="text"
            value={sat}
            onChange={(e) => setSat(e.target.value)}
          />
          <p><strong>Predicted Admission Probability:</strong> {getAdmissionProbability()}</p>
        </div>
      )}
    </div>
  );
};

export default App;
