// ✅ FINAL UPDATED App.js with all features restored and added as requested

import React, { useState } from "react";
import { saveAs } from "file-saver";
import * as XLSX from "xlsx";
import jsPDF from "jspdf";
import html2canvas from "html2canvas";
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';

const universities = [
  {
    name: "Massachusetts Institute of Technology (MIT)",
    subjects: {
      "Computer Science": {
        rankings: { "2021": 1, "2022": 1, "2023": 1, "2024": 1, "2025": 1 },
        gpaCutoff: 3.9,
        satCutoff: 1550,
        intake: "September",
        fees: "$53,790",
        eligibility: {
          international: "TOEFL, SAT, GPA > 3.5",
          domestic: "SAT, GPA > 3.5"
        },
        link: "https://mit.edu/computer-science"
      },
      "Mathematics": {
        rankings: { "2021": 1, "2022": 1, "2023": 1, "2024": 2, "2025": 2 },
        gpaCutoff: 3.8,
        satCutoff: 1500,
        intake: "August",
        fees: "$51,200",
        eligibility: {
          international: "IELTS, GPA > 3.6",
          domestic: "GPA > 3.6"
        },
        link: "https://mit.edu/mathematics"
      }
    },
    generalRank: 1
  }
];

const App = () => {
  const [selectedUniversity, setSelectedUniversity] = useState(null);
  const [selectedSubject, setSelectedSubject] = useState(null);
  const [studentType, setStudentType] = useState("international");
  const [gpa, setGpa] = useState(0);
  const [sat, setSat] = useState(0);
  const [cgpa, setCgpa] = useState(0);

  const handleExcelDownload = () => {
    if (!selectedUniversity || !selectedSubject) return;
    const subjectData = selectedUniversity.subjects[selectedSubject];
    const wsData = [
      ["Year", "QS Rank"],
      ...Object.entries(subjectData.rankings)
    ];
    const ws = XLSX.utils.aoa_to_sheet(wsData);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Rankings");
    XLSX.writeFile(wb, `${selectedUniversity.name}-${selectedSubject}-Rankings.xlsx`);
  };

  const handlePDFDownload = () => {
    const input = document.getElementById("exportable-section");
    html2canvas(input).then((canvas) => {
      const imgData = canvas.toDataURL("image/png");
      const pdf = new jsPDF();
      pdf.addImage(imgData, "PNG", 10, 10);
      pdf.save("university-info.pdf");
    });
  };

  const calculateAdmissionProbability = () => {
    if (!selectedUniversity || !selectedSubject) return "";
    const subjectData = selectedUniversity.subjects[selectedSubject];
    let score = 0;
    if (gpa >= subjectData.gpaCutoff) score += 1;
    if (sat >= subjectData.satCutoff) score += 1;
    if (score === 2) return "High";
    if (score === 1) return "Moderate";
    return "Low";
  };

  const selectedSubjectData = selectedUniversity?.subjects?.[selectedSubject];

  const chartData = selectedSubjectData
    ? Object.entries(selectedSubjectData.rankings).map(([year, rank]) => ({ year, rank }))
    : [];

  return (
    <div style={{ padding: 20 }}>
      <h1>QS World University Rankings</h1>

      <div>
        <label>University: </label>
        <select
          onChange={(e) => {
            const uni = universities.find((u) => u.name === e.target.value);
            setSelectedUniversity(uni);
            setSelectedSubject(null);
          }}
        >
          <option value="">Select University</option>
          {universities.map((u) => (
            <option key={u.name}>{u.name}</option>
          ))}
        </select>
      </div>

      {selectedUniversity && (
        <>
          <div>
            <label>Subject: </label>
            <select onChange={(e) => setSelectedSubject(e.target.value)}>
              <option value="">Select Subject</option>
              {Object.keys(selectedUniversity.subjects).map((subj) => (
                <option key={subj}>{subj}</option>
              ))}
            </select>
          </div>
          <div>
            <label>Student Type: </label>
            <select onChange={(e) => setStudentType(e.target.value)}>
              <option value="international">International</option>
              <option value="domestic">Domestic</option>
            </select>
          </div>
        </>
      )}

      {selectedSubjectData && (
        <div id="exportable-section" style={{ marginTop: 20 }}>
          <h2>{selectedUniversity.name} - {selectedSubject}</h2>
          <p><strong>General QS Rank:</strong> {selectedUniversity.generalRank}</p>
          <p><strong>Intake:</strong> {selectedSubjectData.intake}</p>
          <p><strong>Fees:</strong> {selectedSubjectData.fees}</p>
          <p><strong>Eligibility:</strong> {selectedSubjectData.eligibility[studentType]}</p>
          <p><strong>Course Link:</strong> <a href={selectedSubjectData.link} target="_blank" rel="noreferrer">Visit</a></p>

          <h3>QS Subject Rankings (Past 5 Years)</h3>
          <table border="1" cellPadding="5">
            <thead>
              <tr>
                {Object.keys(selectedSubjectData.rankings).map((year) => (
                  <th key={year}>{year}</th>
                ))}
              </tr>
            </thead>
            <tbody>
              <tr>
                {Object.values(selectedSubjectData.rankings).map((rank, idx) => (
                  <td key={idx}>{rank}</td>
                ))}
              </tr>
            </tbody>
          </table>

          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={chartData} margin={{ top: 20, right: 30, left: 0, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="year" />
              <YAxis reversed={true} domain={[1, 'auto']} />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="rank" stroke="#8884d8" activeDot={{ r: 8 }} />
            </LineChart>
          </ResponsiveContainer>

          <div style={{ marginTop: 20 }}>
            <label>Enter GPA: </label>
            <input type="number" step="0.1" value={gpa} onChange={(e) => setGpa(parseFloat(e.target.value))} />
            <br />
            <label>Enter SAT Score: </label>
            <input type="number" value={sat} onChange={(e) => setSat(parseInt(e.target.value))} />
            <br />
            <p><strong>Admission Probability:</strong> {calculateAdmissionProbability()}</p>
          </div>

          <div style={{ marginTop: 20 }}>
            <label>Enter GPA to Convert to CGPA (/10): </label>
            <input
              type="number"
              step="0.1"
              onChange={(e) => setCgpa((parseFloat(e.target.value) / 4) * 10)}
            />
            <p><strong>Equivalent CGPA:</strong> {cgpa.toFixed(2)}</p>
          </div>
        </div>
      )}

      <div style={{ marginTop: 20 }}>
        <button onClick={handleExcelDownload}>Download Excel</button>
        <button onClick={handlePDFDownload}>Download PDF</button>
      </div>
    </div>
  );
};

export default App;
