import React, { useState, useEffect } from "react";
import UniversityData from "./UniversityData";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [selectedUniversity, setSelectedUniversity] = useState(
    UniversityData[0]?.name || ""
  );
  const [studentType, setStudentType] = useState("International");
  const [selectedSubject, setSelectedSubject] = useState("");
  const [inputCGPA, setInputCGPA] = useState(0);
  const [inputSAT, setInputSAT] = useState(0);

  const university = UniversityData.find(
    (uni) => uni.name === selectedUniversity
  );

  const allSubjects = university?.subjects || [];
  const subject = allSubjects.find((subj) => subj.name === selectedSubject);

  // ðŸ” Reset subject when university changes
  useEffect(() => {
    setSelectedSubject("");
  }, [selectedUniversity]);

  const chartData = subject
    ? {
        labels: subject.qsRankingYears || [],
        datasets: [
          {
            label: "QS Ranking",
            data: subject.qsRankings || [],
            backgroundColor: "rgba(75, 192, 192, 0.6)"
          }
        ]
      }
    : { labels: [], datasets: [] };

  const convertCGPAToGPA = (cgpa) => (cgpa / 10) * 4;

  const estimateProbability = () => {
    if (!subject) return null;
    const gpa = convertCGPAToGPA(inputCGPA);
    const latestGPA = subject.gpaCutoffs?.[0] || 0;
    const latestSAT = subject.satCutoffs?.[0] || 0;

    if (gpa >= latestGPA && inputSAT >= latestSAT) return "85-95%";
    if (gpa >= latestGPA || inputSAT >= latestSAT) return "65-80%";
    return "<50%";
  };

  const getAdmissionCategory = () => {
    const prob = estimateProbability();
    if (prob === "85-95%") return "Highly Likely";
    if (prob === "65-80%") return "Likely";
    return "Doubtful";
  };

  const handleExport = () => {
    const exportData = {
      university: selectedUniversity,
      generalQSRank: university?.generalQSRank,
      subject: selectedSubject,
      fees: subject?.fees?.[studentType],
      eligibility: subject?.eligibility?.[studentType],
      intakesPerYear: subject?.intakesPerYear,
      applicationDeadlines: subject?.applicationDeadlines,
      scholarship: subject?.scholarship,
      startDate: subject?.startDate,
      admissionProbability: estimateProbability(),
      category: getAdmissionCategory()
    };
    const dataStr =
      "data:text/json;charset=utf-8," +
      encodeURIComponent(JSON.stringify(exportData, null, 2));
    const downloadAnchorNode = document.createElement("a");
    downloadAnchorNode.setAttribute("href", dataStr);
    downloadAnchorNode.setAttribute(
      "download",
      `${selectedUniversity}_${selectedSubject}_data.json`
    );
    document.body.appendChild(downloadAnchorNode);
    downloadAnchorNode.click();
    downloadAnchorNode.remove();
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial", lineHeight: 1.6 }}>
      <h1 style={{ textAlign: "center", marginBottom: "30px" }}>
        QS World University Rankings Explorer
      </h1>

      <div style={{ marginBottom: "20px" }}>
        <label><b>Select University:</b></label>
        <select
          value={selectedUniversity}
          onChange={(e) => setSelectedUniversity(e.target.value)}
          style={{ marginLeft: "10px" }}
        >
          {UniversityData.map((uni) => (
            <option key={uni.name} value={uni.name}>
              {uni.name}
            </option>
          ))}
        </select>

        <div style={{ marginTop: "10px" }}>
          <label><b>Student Type:</b></label>
          <label style={{ marginLeft: "10px" }}>
            <input
              type="radio"
              value="International"
              checked={studentType === "International"}
              onChange={(e) => setStudentType(e.target.value)}
            />
            International
          </label>
          <label style={{ marginLeft: "10px" }}>
            <input
              type="radio"
              value="Domestic"
              checked={studentType === "Domestic"}
              onChange={(e) => setStudentType(e.target.value)}
            />
            Domestic
          </label>
        </div>
      </div>

      {university && (
        <>
          <h2>{university.name}</h2>
          <p><b>General QS Rank:</b> {university.generalQSRank}</p>

          <label><b>Select Subject:</b></label>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
            style={{ marginLeft: "10px" }}
          >
            <option value="">-- Select a Subject --</option>
            {allSubjects.map((subj) => (
              <option key={subj.name} value={subj.name}>
                {subj.name}
              </option>
            ))}
          </select>

          {subject && (
            <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "8px", marginTop: "20px" }}>
              <h3>{selectedSubject}</h3>
              <p><b>QS Subject Rank:</b> {subject.qsRankings?.[0]}</p>
              <p><b>Start Date:</b> {subject.startDate}</p>
              <p><b>Fees:</b> {subject.fees?.[studentType]}</p>
              <p><b>Eligibility:</b> {subject.eligibility?.[studentType]}</p>
              <p><b>Intakes per Year:</b> {subject.intakesPerYear}</p>
              <p><b>Application Deadlines:</b> {subject.applicationDeadlines}</p>
              <p><b>Scholarship Info:</b> {subject.scholarship || "Not Available"}</p>

              <div style={{ marginTop: "20px" }}>
                <h4>Student Inputs</h4>
                <label>Enter CGPA (out of 10): </label>
                <input
                  type="number"
                  value={inputCGPA}
                  onChange={(e) => setInputCGPA(Number(e.target.value))}
                  step="0.1"
                  min="0"
                  max="10"
                  style={{ marginLeft: "10px" }}
                />
                {inputCGPA > 0 && (
                  <p>
                    Converted GPA (US Scale): <b>{convertCGPAToGPA(inputCGPA).toFixed(2)}</b>
                  </p>
                )}

                <div style={{ marginTop: "10px" }}>
                  <label>Enter SAT Score: </label>
                  <input
                    type="number"
                    value={inputSAT}
                    onChange={(e) => setInputSAT(Number(e.target.value))}
                    step="10"
                    min="0"
                    max="1600"
                    style={{ marginLeft: "10px" }}
                  />
                </div>

                {inputCGPA > 0 && inputSAT > 0 ? (
                  <>
                    <p>
                      Estimated Admission Probability: <b>{estimateProbability()}</b>
                    </p>
                    <p>
                      Admission Category: <b>{getAdmissionCategory()}</b>
                    </p>
                  </>
                ) : (
                  <p style={{ color: "gray" }}>
                    Enter both CGPA and SAT to estimate admission probability
                  </p>
                )}
              </div>

              <p>
                Course Link: <a href={subject.courseLink}>{subject.courseLink}</a>
              </p>

              <button onClick={handleExport} style={{ marginTop: "10px" }}>
                Save/Export Info
              </button>

              <h4>QS Rankings (Last 5 Years)</h4>
              <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
                <thead style={{ backgroundColor: "#f0f0f0" }}>
                  <tr>
                    <th>Year</th>
                    <th>QS Rank</th>
                    <th>GPA Cutoff</th>
                    <th>SAT Cutoff</th>
                  </tr>
                </thead>
                <tbody>
                  {subject.qsRankingYears?.map((year, index) => (
                    <tr key={year}>
                      <td>{year}</td>
                      <td>{subject.qsRankings?.[index]}</td>
                      <td>{subject.gpaCutoffs?.[index]}</td>
                      <td>{subject.satCutoffs?.[index]}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div style={{ maxWidth: "600px", marginTop: "30px" }}>
                <Bar data={chartData} />
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
