import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import StudentTypeToggle from "./StudentTypeToggle";
import universityData from "./UniversityData";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const App = () => {
  const [selectedUniversity, setSelectedUniversity] = useState("");
  const [selectedSubject, setSelectedSubject] = useState("");
  const [studentType, setStudentType] = useState("international");
  const [cgpa, setCgpa] = useState("");
  const [convertedGPA, setConvertedGPA] = useState(null);
  const [gpaProbability, setGpaProbability] = useState(null);
  const [satScore, setSatScore] = useState("");
  const [mathScore, setMathScore] = useState("");
  const [toeflScore, setToeflScore] = useState("");
  const [finalProbability, setFinalProbability] = useState(null);

  const handleScoreChange = (setter, scoreType) => (e) => {
    const value = e.target.value;
    setter(value);

    let updatedScores = {
      sat: scoreType === "sat" ? value : satScore,
      math: scoreType === "math" ? value : mathScore,
      toefl: scoreType === "toefl" ? value : toeflScore,
    };

    setTimeout(() => {
      computeFinalProbability(
        convertedGPA ? parseFloat(convertedGPA) : 0,
        updatedScores.sat,
        updatedScores.math,
        updatedScores.toefl
      );
    }, 0);
  };

  const handleCgpaChange = (e) => {
    const value = parseFloat(e.target.value);
    setCgpa(e.target.value);
    if (!isNaN(value)) {
      const gpa = (value / 10) * 4;
      setConvertedGPA(gpa.toFixed(2));

      const subjectData =
        universityData[selectedUniversity]?.subjects[selectedSubject];
      if (subjectData) {
        const min = subjectData.minGPA;
        const prob = Math.max(0, Math.min(1, (gpa - min) / (4.0 - min)));
        setGpaProbability((prob * 100).toFixed(1));
        computeFinalProbability(gpa, satScore, mathScore, toeflScore);
      } else {
        setGpaProbability(null);
        setFinalProbability(null);
      }
    } else {
      setConvertedGPA(null);
      setGpaProbability(null);
      setFinalProbability(null);
    }
  };

  const computeFinalProbability = (gpa, sat, math, toefl) => {
    const gpaScore = gpa ? (gpa / 4) : 0;
    const satScoreNorm = sat ? (parseInt(sat) / 1600) : 0;
    const mathScoreNorm = math ? (parseInt(math) / 100) : 0;
    const toeflScoreNorm = toefl ? (parseInt(toefl) / 120) : 0;
    const avg = (gpaScore + satScoreNorm + mathScoreNorm + toeflScoreNorm) / 4;
    setFinalProbability((avg * 100).toFixed(1));
  };

  const subjectData =
    universityData[selectedUniversity]?.subjects[selectedSubject];

  const chartData = {
    labels: subjectData ? Object.keys(subjectData.rankings) : [],
    datasets: [
      {
        label: "QS Subject Rank",
        data: subjectData ? Object.values(subjectData.rankings) : [],
        borderColor: "#3e95cd",
        backgroundColor: "#3e95cd",
        fill: false,
        tension: 0.3,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "QS Subject Rankings Over Years" },
    },
    scales: {
      y: {
        reverse: true,
        title: {
          display: true,
          text: "Rank",
        },
      },
      x: {
        title: {
          display: true,
          text: "Year",
        },
      },
    },
  };

  return (
    <div style={{ padding: 20, maxWidth: 1000, margin: "auto" }}>
      <h1 className="text-2xl font-bold mb-4">QS World University Rankings Explorer</h1>

      <div className="mb-4">
        <label>Select University: </label>
        <select
          value={selectedUniversity}
          onChange={(e) => {
            setSelectedUniversity(e.target.value);
            setSelectedSubject("");
          }}
        >
          <option value="">-- Select --</option>
          {Object.keys(universityData).map((univ) => (
            <option key={univ} value={univ}>
              {univ}
            </option>
          ))}
        </select>
        {selectedUniversity && (
          <p className="mt-2">General QS Rank: {universityData[selectedUniversity].generalQSRank}</p>
        )}
      </div>

      {selectedUniversity && (
        <div className="mb-4">
          <label>Select Subject: </label>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
          >
            <option value="">-- Select --</option>
            {Object.keys(universityData[selectedUniversity].subjects).map(
              (subj) => (
                <option key={subj} value={subj}>
                  {subj}
                </option>
              )
            )}
          </select>
        </div>
      )}

      <StudentTypeToggle
        studentType={studentType}
        onToggle={(type) => setStudentType(type)}
      />

      {subjectData && (
        <div className="mb-8">
          <h2 className="text-xl font-semibold">{selectedSubject}</h2>
          <p><strong>Fees:</strong> {subjectData.fees[studentType]}</p>
          <p><strong>Intake Months:</strong> {subjectData.intakes.join(", ")}</p>
          <p><strong>Application Deadlines:</strong> {subjectData.deadlines.join(", ")}</p>
          <p><strong>Eligibility:</strong> {subjectData.eligibility[studentType]}</p>
          <p><strong>Course Link:</strong> <a href={subjectData.courseLink} target="_blank" rel="noreferrer">Visit Course Page</a></p>
          <p><strong>Minimum GPA Required:</strong> {subjectData.minGPA}</p>

          <h3 className="mt-4 font-semibold">Scholarships:</h3>
          <ul className="list-disc list-inside">
            {subjectData.scholarships.map((s, idx) => (
              <li key={idx}>{s}</li>
            ))}
          </ul>

          <h3 className="mt-4 font-semibold">Application Checklist:</h3>
          <ul className="list-disc list-inside">
            {subjectData.applicationChecklist.map((item, idx) => (
              <li key={idx}>{item}</li>
            ))}
          </ul>

          <div className="my-6">
            <Line data={chartData} options={chartOptions} height={250} />
          </div>

          <div className="my-4">
            <label className="block mb-2">Enter your CGPA (out of 10):</label>
            <input
              type="number"
              step="0.01"
              value={cgpa}
              onChange={handleCgpaChange}
              className="border px-2 py-1"
            />
            {convertedGPA && (
              <p>Your converted GPA: <strong>{convertedGPA}</strong></p>
            )}
            {gpaProbability && (
              <p>Estimated admission probability based on GPA: <strong>{gpaProbability}%</strong></p>
            )}
          </div>

          <div className="my-4">
            <label className="block">Enter SAT Score (out of 1600):</label>
            <input type="number" value={satScore} onChange={handleScoreChange(setSatScore, "sat")} className="border px-2 py-1" />

            <label className="block mt-2">Enter 12th Grade Math Score (out of 100):</label>
            <input type="number" value={mathScore} onChange={handleScoreChange(setMathScore, "math")} className="border px-2 py-1" />

            <label className="block mt-2">Enter TOEFL Score (out of 120):</label>
            <input type="number" value={toeflScore} onChange={handleScoreChange(setToeflScore, "toefl")} className="border px-2 py-1" />

            {finalProbability && (
              <p className="mt-2">Estimated Overall Admission Probability: <strong>{finalProbability}%</strong></p>
            )}
          </div>

          <div className="mt-4">
            <p><strong>Average SAT Score:</strong> {subjectData.satScores ? Object.values(subjectData.satScores).slice(-1)[0] : "N/A"}</p>
            <p><strong>Average GPA Score:</strong> {subjectData.gpaScores ? Object.values(subjectData.gpaScores).slice(-1)[0] : "N/A"}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
