import React, { useState } from "react";
import UniversityData from "./UniversityData";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";
import { Bar } from "react-chartjs-2";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [selectedUniversity, setSelectedUniversity] = useState(
    UniversityData[0]?.name || ""
  );
  const [studentType, setStudentType] = useState("International");
  const [selectedSubject, setSelectedSubject] = useState(
    UniversityData[0]?.subjects[0]?.name || ""
  );
  const [inputCGPA, setInputCGPA] = useState(0);
  const [inputSAT, setInputSAT] = useState(0);

  const university = UniversityData.find(
    (uni) => uni.name === selectedUniversity
  );
  const subject = university?.subjects.find(
    (subj) => subj.name === selectedSubject
  );

  const chartData = subject
    ? {
        labels: subject.qsRankingYears || [],
        datasets: [
          {
            label: "QS Ranking",
            data: subject.qsRankings || [],
            backgroundColor: "rgba(75, 192, 192, 0.6)"
          }
        ]
      }
    : { labels: [], datasets: [] };

  const convertCGPAToGPA = (cgpa) => (cgpa / 10) * 4;

  const estimateProbability = () => {
    if (!subject) return null;
    const gpa = convertCGPAToGPA(inputCGPA);
    const latestGPA = subject.gpaCutoffs?.[0] || 0;
    const latestSAT = subject.satCutoffs?.[0] || 0;

    if (gpa >= latestGPA && inputSAT >= latestSAT) return "85-95%";
    if (gpa >= latestGPA || inputSAT >= latestSAT) return "65-80%";
    return "<50%";
  };

  const getAdmissionCategory = () => {
    const prob = estimateProbability();
    if (prob === "85-95%") return "Highly Likely";
    if (prob === "65-80%") return "Likely";
    return "Doubtful";
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>QS World University Rankings Explorer</h1>

      <label>Select University: </label>
      <select
        value={selectedUniversity}
        onChange={(e) => {
          const newUni = e.target.value;
          setSelectedUniversity(newUni);
          const firstSubject =
            UniversityData.find((u) => u.name === newUni)?.subjects[0]?.name ||
            "";
          setSelectedSubject(firstSubject);
        }}
      >
        {UniversityData.map((uni) => (
          <option key={uni.name} value={uni.name}>
            {uni.name}
          </option>
        ))}
      </select>

      <div style={{ margin: "10px 0" }}>
        <label>
          <input
            type="radio"
            value="International"
            checked={studentType === "International"}
            onChange={(e) => setStudentType(e.target.value)}
          />
          International
        </label>
        <label style={{ marginLeft: "10px" }}>
          <input
            type="radio"
            value="Domestic"
            checked={studentType === "Domestic"}
            onChange={(e) => setStudentType(e.target.value)}
          />
          Domestic
        </label>
      </div>

      {university && (
        <>
          <h2>{university.name}</h2>
          <label>Select Subject:</label>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
          >
            {university.subjects.map((subj) => (
              <option key={subj.name} value={subj.name}>
                {subj.name}
              </option>
            ))}
          </select>

          {subject && (
            <>
              <h3>{selectedSubject}</h3>
              <p>Start Date: {subject.startDate}</p>
              <p>Fees: {subject.fees?.[studentType]}</p>
              <p>Eligibility: {subject.eligibility?.[studentType]}</p>
              <p>Intakes per Year: {subject.intakesPerYear}</p>
              <p>Application Deadlines: {subject.applicationDeadlines}</p>

              <div style={{ marginTop: "20px", marginBottom: "20px" }}>
                <h4>Student Inputs</h4>

                <div style={{ marginBottom: "10px" }}>
                  <label>Enter CGPA (out of 10): </label>
                  <input
                    type="number"
                    value={inputCGPA}
                    onChange={(e) => setInputCGPA(Number(e.target.value))}
                    step="0.1"
                    min="0"
                    max="10"
                  />
                  {inputCGPA > 0 && (
                    <p>
                      Converted GPA (US Scale):{" "}
                      <b>{convertCGPAToGPA(inputCGPA).toFixed(2)}</b>
                    </p>
                  )}
                </div>

                <div style={{ marginBottom: "10px" }}>
                  <label>Enter SAT Score: </label>
                  <input
                    type="number"
                    value={inputSAT}
                    onChange={(e) => setInputSAT(Number(e.target.value))}
                    step="10"
                    min="0"
                    max="1600"
                  />
                </div>

                {inputCGPA > 0 && inputSAT > 0 ? (
                  <>
                    <p>
                      Estimated Admission Probability:{" "}
                      <b>{estimateProbability()}</b>
                    </p>
                    <p>
                      Admission Category:{" "}
                      <b>{getAdmissionCategory()}</b>
                    </p>
                  </>
                ) : (
                  <p style={{ color: "gray" }}>
                    Enter both CGPA and SAT to estimate admission probability
                  </p>
                )}
              </div>

              <p>
                Course Link:{" "}
                <a href={subject.courseLink} target="_blank" rel="noreferrer">
                  {subject.courseLink}
                </a>
              </p>

              <h4>QS Rankings (Last 5 Years)</h4>
              <table border="1" cellPadding="5">
                <thead>
                  <tr>
                    <th>Year</th>
                    <th>QS Rank</th>
                    <th>GPA Cutoff</th>
                    <th>SAT Cutoff</th>
                  </tr>
                </thead>
                <tbody>
                  {subject.qsRankingYears?.map((year, index) => (
                    <tr key={year}>
                      <td>{year}</td>
                      <td>{subject.qsRankings?.[index]}</td>
                      <td>{subject.gpaCutoffs?.[index]}</td>
                      <td>{subject.satCutoffs?.[index]}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div style={{ maxWidth: "600px", marginTop: "30px" }}>
                <Bar data={chartData} />
              </div>
            </>
          )}
        </>
      )}
    </div>
  );
}

export default App;
