import React, { useState } from "react";
import jsPDF from "jspdf";
import * as XLSX from "xlsx";

const universities = [
  {
    name: "Massachusetts Institute of Technology (MIT)",
    generalRank: 1,
    subjects: [
      {
        name: "Computer Science",
        qsRankings: {
          2025: 1,
          2024: 1,
          2023: 2,
          2022: 1,
          2021: 1,
        },
        gpaCutoffs: {
          2025: 3.9,
          2024: 3.8,
          2023: 3.8,
          2022: 3.7,
          2021: 3.6,
        },
        satCutoffs: {
          2025: 1550,
          2024: 1530,
          2023: 1500,
          2022: 1480,
          2021: 1450,
        },
        fees: "$53,790",
        intake: "Fall 2025",
        eligibility: {
          international: "TOEFL, SOP, LORs, Transcripts",
          domestic: "SAT/ACT, GPA, Transcripts",
        },
      },
      {
        name: "Mathematics",
        qsRankings: {
          2025: 1,
          2024: 2,
          2023: 2,
          2022: 1,
          2021: 1,
        },
        gpaCutoffs: {
          2025: 3.8,
          2024: 3.7,
          2023: 3.7,
          2022: 3.6,
          2021: 3.6,
        },
        satCutoffs: {
          2025: 1520,
          2024: 1500,
          2023: 1480,
          2022: 1460,
          2021: 1450,
        },
        fees: "$52,000",
        intake: "Fall 2025",
        eligibility: {
          international: "TOEFL, SOP, LORs, Transcripts",
          domestic: "SAT/ACT, GPA, Transcripts",
        },
      },
    ],
  },
  {
    name: "Stanford University",
    generalRank: 3,
    subjects: [
      {
        name: "Computer Science",
        qsRankings: {
          2025: 2,
          2024: 2,
          2023: 3,
          2022: 2,
          2021: 2,
        },
        gpaCutoffs: {
          2025: 3.9,
          2024: 3.8,
          2023: 3.8,
          2022: 3.7,
          2021: 3.7,
        },
        satCutoffs: {
          2025: 1540,
          2024: 1520,
          2023: 1500,
          2022: 1470,
          2021: 1450,
        },
        fees: "$56,169",
        intake: "Fall 2025",
        eligibility: {
          international: "TOEFL, SOP, LORs, Transcripts",
          domestic: "SAT/ACT, GPA, Transcripts",
        },
      },
    ],
  },
];

function App() {
  const [selectedUniversity, setSelectedUniversity] = useState("");
  const [selectedSubject, setSelectedSubject] = useState("");
  const [studentType, setStudentType] = useState("international");
  const [cgpa, setCgpa] = useState("");
  const [satScore, setSatScore] = useState("");
  const [reports, setReports] = useState([]);

  const handleSaveReport = () => {
    const university = universities.find((u) => u.name === selectedUniversity);
    const subject = university?.subjects.find((s) => s.name === selectedSubject);

    if (!university || !subject || !cgpa || !satScore) return;

    const avgGpa = Object.values(subject.gpaCutoffs).reduce((a, b) => a + b, 0) / 5;
    const avgSat = Object.values(subject.satCutoffs).reduce((a, b) => a + b, 0) / 5;

    const admissionProbability =
      (parseFloat(cgpa) >= avgGpa && parseInt(satScore) >= avgSat)
        ? "High"
        : (parseFloat(cgpa) >= avgGpa - 0.2 || parseInt(satScore) >= avgSat - 50)
        ? "Moderate"
        : "Low";

    const newReport = {
      university: university.name,
      subject: subject.name,
      generalRank: university.generalRank,
      qsRankings: subject.qsRankings,
      gpaCutoffs: subject.gpaCutoffs,
      satCutoffs: subject.satCutoffs,
      fees: subject.fees,
      intake: subject.intake,
      eligibility: subject.eligibility[studentType],
      cgpa,
      satScore,
      admissionProbability,
    };

    setReports([...reports, newReport]);
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    reports.forEach((report, i) => {
      doc.text(`University: ${report.university}`, 10, 10 + i * 60);
      doc.text(`Subject: ${report.subject}`, 10, 20 + i * 60);
      doc.text(`QS General Rank: ${report.generalRank}`, 10, 30 + i * 60);
      doc.text(`Admission Probability: ${report.admissionProbability}`, 10, 40 + i * 60);
    });
    doc.save("report.pdf");
  };

  const exportExcel = () => {
    const worksheet = XLSX.utils.json_to_sheet(reports);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "Reports");
    XLSX.writeFile(workbook, "report.xlsx");
  };

  const selectedUni = universities.find((u) => u.name === selectedUniversity);
  const selectedSub = selectedUni?.subjects.find((s) => s.name === selectedSubject);

  return (
    <div className="App" style={{ padding: 20 }}>
      <h2>QS University Admission Predictor</h2>

      <div>
        <label>University:</label>
        <select onChange={(e) => setSelectedUniversity(e.target.value)} value={selectedUniversity}>
          <option value="">Select</option>
          {universities.map((u, idx) => (
            <option key={idx} value={u.name}>{u.name}</option>
          ))}
        </select>
      </div>

      {selectedUniversity && (
        <div>
          <p><strong>QS General Rank:</strong> #{selectedUni.generalRank}</p>
        </div>
      )}

      {selectedUniversity && (
        <div>
          <label>Subject:</label>
          <select onChange={(e) => setSelectedSubject(e.target.value)} value={selectedSubject}>
            <option value="">Select</option>
            {selectedUni.subjects.map((s, idx) => (
              <option key={idx} value={s.name}>{s.name}</option>
            ))}
          </select>
        </div>
      )}

      {selectedSub && (
        <div style={{ marginTop: 20 }}>
          <h3>{selectedSub.name} - Last 5 Years</h3>
          <table border="1" cellPadding="5">
            <thead>
              <tr>
                <th>Year</th>
                <th>QS Rank</th>
                <th>GPA Cutoff</th>
                <th>SAT Cutoff</th>
              </tr>
            </thead>
            <tbody>
              {Object.keys(selectedSub.qsRankings).map((year) => (
                <tr key={year}>
                  <td>{year}</td>
                  <td>{selectedSub.qsRankings[year]}</td>
                  <td>{selectedSub.gpaCutoffs[year]}</td>
                  <td>{selectedSub.satCutoffs[year]}</td>
                </tr>
              ))}
            </tbody>
          </table>

          <p><strong>Fees:</strong> {selectedSub.fees}</p>
          <p><strong>Intake:</strong> {selectedSub.intake}</p>
          <p><strong>Eligibility ({studentType}):</strong> {selectedSub.eligibility[studentType]}</p>
        </div>
      )}

      <div>
        <label>Student Type:</label>
        <select value={studentType} onChange={(e) => setStudentType(e.target.value)}>
          <option value="international">International</option>
          <option value="domestic">Domestic</option>
        </select>
      </div>

      <div>
        <label>CGPA:</label>
        <input type="number" value={cgpa} onChange={(e) => setCgpa(e.target.value)} />
      </div>

      <div>
        <label>SAT Score:</label>
        <input type="number" value={satScore} onChange={(e) => setSatScore(e.target.value)} />
      </div>

      <button onClick={handleSaveReport}>Save Report</button>
      <button onClick={exportPDF}>Export PDF</button>
      <button onClick={exportExcel}>Export Excel</button>

      <h3>Saved Reports</h3>
      <table border="1" cellPadding="5">
        <thead>
          <tr>
            <th>University</th>
            <th>Subject</th>
            <th>QS General Rank</th>
            <th>GPA</th>
            <th>SAT</th>
            <th>Probability</th>
            <th>Fees</th>
            <th>Intake</th>
            <th>Eligibility</th>
          </tr>
        </thead>
        <tbody>
          {reports.map((r, i) => (
            <tr key={i}>
              <td>{r.university}</td>
              <td>{r.subject}</td>
              <td>{r.generalRank}</td>
              <td>{r.cgpa}</td>
              <td>{r.satScore}</td>
              <td>{r.admissionProbability}</td>
              <td>{r.fees}</td>
              <td>{r.intake}</td>
              <td>{r.eligibility}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
