import React, { useState } from "react";
import universities from "./UniversityData";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  Legend
} from "recharts";

const App = () => {
  const [selectedUniversity, setSelectedUniversity] = useState(null);
  const [studentType, setStudentType] = useState("International");
  const [selectedSubject, setSelectedSubject] = useState("");
  const [cgpa, setCgpa] = useState("");
  const [satScore, setSatScore] = useState("");

  const handleUniversityChange = (e) => {
    const university = universities.find((u) => u.name === e.target.value);
    setSelectedUniversity(university);
    setSelectedSubject(""); // reset subject on university change
  };

  const convertCGPAToGPA = (cgpa) => (cgpa / 10) * 4;

  const calculateAdmissionProbability = (subject) => {
    if (!subject || !cgpa || !satScore) return null;

    const gpa = convertCGPAToGPA(parseFloat(cgpa));
    const lastYear = subject.rankings[subject.rankings.length - 1];

    const meetsSAT = parseInt(satScore) >= lastYear.satCutoff;
    const meetsGPA = gpa >= lastYear.gpaCutoff;

    if (meetsSAT && meetsGPA) return "Likely";
    if (meetsSAT || meetsGPA) return "Doubtful";
    return "Highly Unlikely";
  };

  const selectedSubjectData =
    selectedUniversity?.subjects.find((s) => s.name === selectedSubject) || null;

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>QS World University Rankings Explorer</h2>

      {/* University Selector */}
      <label>Select University: </label>
      <select onChange={handleUniversityChange} value={selectedUniversity?.name || ""}>
        <option value="">-- Select --</option>
        {universities.map((uni) => (
          <option key={uni.name} value={uni.name}>
            {uni.name}
          </option>
        ))}
      </select>

      {/* Student Type */}
      {selectedUniversity && (
        <div>
          <label>
            <input
              type="radio"
              checked={studentType === "International"}
              onChange={() => setStudentType("International")}
            />
            International
          </label>
          <label style={{ marginLeft: "10px" }}>
            <input
              type="radio"
              checked={studentType === "Domestic"}
              onChange={() => setStudentType("Domestic")}
            />
            Domestic
          </label>
        </div>
      )}

      {/* Display University and General QS Rank */}
      {selectedUniversity && (
        <>
          <h3>{selectedUniversity.name}</h3>
          <p><strong>General QS Rank:</strong> {selectedUniversity.generalQSRank}</p>

          {/* Subject Dropdown */}
          <label>Select Subject: </label>
          <select
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
          >
            <option value="">-- Select --</option>
            {selectedUniversity.subjects.map((s) => (
              <option key={s.name} value={s.name}>
                {s.name}
              </option>
            ))}
          </select>
        </>
      )}

      {/* Subject Info */}
      {selectedSubjectData && (
        <>
          <p><strong>QS Subject Rank:</strong> {selectedSubjectData.qsSubjectRank}</p>
          <p><strong>Start Date:</strong> {selectedSubjectData.startDate}</p>
          <p><strong>Fees:</strong> {selectedSubjectData.fees}</p>
          <p>
            <strong>Eligibility:</strong> {selectedSubjectData.eligibility[studentType]}
          </p>
          <p><strong>Intakes per Year:</strong> {selectedSubjectData.intakes}</p>
          <p><strong>Application Deadlines:</strong> {selectedSubjectData.deadlines}</p>
          <p><strong>Scholarship Info:</strong> {selectedSubjectData.scholarshipInfo}</p>

          {/* Student Inputs */}
          <div style={{ marginTop: "15px" }}>
            <h4>Student Inputs</h4>
            <label>
              Enter CGPA (out of 10):{" "}
              <input
                type="number"
                value={cgpa}
                onChange={(e) => setCgpa(e.target.value)}
              />
            </label>
            <br />
            <label>
              Enter SAT Score:{" "}
              <input
                type="number"
                value={satScore}
                onChange={(e) => setSatScore(e.target.value)}
              />
            </label>
          </div>

          {/* Admission Probability */}
          <div style={{ marginTop: "10px" }}>
            <h4>Admission Probability</h4>
            {cgpa && satScore ? (
              <p>
                Based on your inputs, your admission chance is:{" "}
                <strong>{calculateAdmissionProbability(selectedSubjectData)}</strong>
              </p>
            ) : (
              <p>Enter both CGPA and SAT to estimate admission probability</p>
            )}
          </div>

          {/* QS Rankings Table */}
          <h4>QS Rankings (Last 5 Years)</h4>
          <table border="1" cellPadding="5" style={{ borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th>Year</th>
                <th>QS Rank</th>
                <th>GPA Cutoff</th>
                <th>SAT Cutoff</th>
              </tr>
            </thead>
            <tbody>
              {selectedSubjectData.rankings.map((r, index) => (
                <tr key={index}>
                  <td>{r.year}</td>
                  <td>{r.qsRank}</td>
                  <td>{r.gpaCutoff}</td>
                  <td>{r.satCutoff}</td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* QS Rankings Chart */}
          <div style={{ height: "300px", marginTop: "20px" }}>
            <h4>QS Rank Trend (Line Graph)</h4>
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={selectedSubjectData.rankings}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="year" />
                <YAxis domain={['dataMin - 1', 'dataMax + 1']} allowDecimals={false} />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="qsRank" stroke="#8884d8" />
              </LineChart>
            </ResponsiveContainer>
          </div>

          {/* Save/Export Placeholder */}
          <p style={{ marginTop: "20px" }}>
            <strong>Course Link:</strong> {selectedSubjectData.courseLink}
          </p>
          <p><strong>Save/Export Info</strong></p>
        </>
      )}
    </div>
  );
};

export default App;
