import React, { useState } from "react";
import { jsPDF } from "jspdf";
import autoTable from "jspdf-autotable";
import * as XLSX from "xlsx";
import universityData from "./UniversityData";

function App() {
  const [selectedUniversity, setSelectedUniversity] = useState(universityData[0]);
  const [selectedSubject, setSelectedSubject] = useState(selectedUniversity.subjects[0]);
  const [selectedStudentType, setSelectedStudentType] = useState("International");
  const [cgpa, setCgpa] = useState(0);
  const [sat, setSat] = useState(0);
  const [reports, setReports] = useState([]);

  const selectedSubjectData = selectedUniversity.subjectDetails[selectedSubject];

  const convertCgpaToGpa = (cgpa) => (cgpa / 10) * 4;

  const estimateAdmissionProbability = () => {
    if (!selectedSubjectData || !cgpa || !sat) return "Enter both CGPA and SAT";
    const gpa = convertCgpaToGpa(cgpa);
    const gpaCutoff = parseFloat(selectedSubjectData.gpaCutoff);
    const satCutoff = parseInt(selectedSubjectData.satCutoff);

    if (gpa >= gpaCutoff && sat >= satCutoff) return "Highly Likely";
    if (gpa >= gpaCutoff - 0.2 || sat >= satCutoff - 30) return "Likely";
    if (gpa >= gpaCutoff - 0.5 || sat >= satCutoff - 100) return "Doubtful";
    return "Unlikely";
  };

  const handleSaveReport = () => {
    const gpa = convertCgpaToGpa(cgpa);
    const probability = estimateAdmissionProbability();

    const newReport = {
      university: selectedUniversity.name,
      subject: selectedSubject,
      studentType: selectedStudentType,
      cgpa,
      gpa: gpa.toFixed(2),
      sat,
      probability,
      subjectData: selectedSubjectData
    };

    setReports([...reports, newReport]);
  };

  const exportToPDF = () => {
    const doc = new jsPDF();

    reports.forEach((report, index) => {
      doc.setFontSize(14);
      doc.setFont("helvetica", "bold");
      doc.text(`University Admission Report #${index + 1}`, 14, 20);
      doc.setFontSize(11);
      doc.setFont("helvetica", "normal");

      autoTable(doc, {
        startY: 30,
        theme: 'grid',
        headStyles: { fillColor: [22, 160, 133], textColor: 255, fontStyle: 'bold' },
        styles: { cellPadding: 3, fontSize: 10 },
        body: [
          ["University", report.university],
          ["Subject", report.subject],
          ["Student Type", report.studentType],
          ["CGPA", report.cgpa],
          ["GPA (4.0)", report.gpa],
          ["SAT Score", report.sat],
          ["Admission Probability", report.probability],
          ["QS Subject Rank", report.subjectData?.qsSubjectRank],
          ["Start Date", report.subjectData?.startDate],
          ["Fees", report.subjectData?.fees?.[report.studentType]],
          ["Eligibility", report.subjectData?.eligibility?.[report.studentType]],
          ["Intakes", report.subjectData?.intakes],
          ["Deadlines", report.subjectData?.deadlines],
          ["Scholarship Info", report.subjectData?.scholarship],
          ["Course Link", report.subjectData?.courseLink]
        ]
      });

      if (index < reports.length - 1) doc.addPage();
    });

    doc.save("university_reports.pdf");
  };

  const exportToExcel = () => {
    if (!reports || reports.length === 0) {
      alert("No data available to export.");
      return;
    }

    const worksheetData = reports.map((report, index) => ({
      Report: `#${index + 1}`,
      University: report.university,
      Subject: report.subject,
      StudentType: report.studentType,
      CGPA: report.cgpa,
      GPA_4_Scale: report.gpa,
      SAT_Score: report.sat,
      Admission_Probability: report.probability,
      QS_Subject_Rank: report.subjectData?.qsSubjectRank,
      Start_Date: report.subjectData?.startDate,
      Fees: report.subjectData?.fees?.[report.studentType],
      Eligibility: report.subjectData?.eligibility?.[report.studentType],
      Intakes: report.subjectData?.intakes,
      Deadlines: report.subjectData?.deadlines,
      Scholarship_Info: report.subjectData?.scholarship,
      Course_Link: report.subjectData?.courseLink
    }));

    const worksheet = XLSX.utils.json_to_sheet(worksheetData);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, "Reports");
    XLSX.writeFile(workbook, "university_reports.xlsx");
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>QS University Admission Predictor</h2>

      <div style={{ marginBottom: "15px" }}>
        <label>University: </label>
        <select value={selectedUniversity.name} onChange={(e) => {
          const selected = universityData.find(u => u.name === e.target.value);
          setSelectedUniversity(selected);
          setSelectedSubject(selected.subjects[0]);
        }}>
          {universityData.map((uni) => (
            <option key={uni.name} value={uni.name}>{uni.name}</option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: "15px" }}>
        <label>Subject: </label>
        <select value={selectedSubject} onChange={(e) => setSelectedSubject(e.target.value)}>
          {selectedUniversity.subjects.map((subj) => (
            <option key={subj} value={subj}>{subj}</option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: "15px" }}>
        <label>Student Type: </label>
        <select value={selectedStudentType} onChange={(e) => setSelectedStudentType(e.target.value)}>
          <option value="International">International</option>
          <option value="Domestic">Domestic</option>
        </select>
      </div>

      <div style={{ marginBottom: "15px" }}>
        <label>CGPA (out of 10): </label>
        <input type="number" value={cgpa} onChange={(e) => setCgpa(Number(e.target.value))} />
      </div>

      <div style={{ marginBottom: "15px" }}>
        <label>SAT Score: </label>
        <input type="number" value={sat} onChange={(e) => setSat(Number(e.target.value))} />
      </div>

      <button onClick={handleSaveReport} style={{ marginRight: "10px" }}>Save Report</button>
      <button onClick={exportToPDF} style={{ marginRight: "10px" }}>Export to PDF</button>
      <button onClick={exportToExcel}>Export to Excel</button>

      <h3 style={{ marginTop: "30px" }}>Saved Reports</h3>
      {reports.length === 0 ? (
        <p>No reports saved yet.</p>
      ) : (
        <table border="1" cellPadding="6" style={{ marginTop: "10px", width: "100%", borderCollapse: "collapse" }}>
          <thead>
            <tr style={{ backgroundColor: "#e0e0e0" }}>
              <th>#</th>
              <th>University</th>
              <th>Subject</th>
              <th>Student Type</th>
              <th>CGPA</th>
              <th>GPA (4.0)</th>
              <th>SAT</th>
              <th>Probability</th>
              <th>QS Rank</th>
              <th>Start Date</th>
              <th>Fees</th>
              <th>Eligibility</th>
              <th>Intakes</th>
              <th>Deadlines</th>
              <th>Scholarship</th>
              <th>Course Link</th>
            </tr>
          </thead>
          <tbody>
            {reports.map((r, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{r.university}</td>
                <td>{r.subject}</td>
                <td>{r.studentType}</td>
                <td>{r.cgpa}</td>
                <td>{r.gpa}</td>
                <td>{r.sat}</td>
                <td>{r.probability}</td>
                <td>{r.subjectData?.qsSubjectRank}</td>
                <td>{r.subjectData?.startDate}</td>
                <td>{r.subjectData?.fees?.[r.studentType]}</td>
                <td>{r.subjectData?.eligibility?.[r.studentType]}</td>
                <td>{r.subjectData?.intakes}</td>
                <td>{r.subjectData?.deadlines}</td>
                <td>{r.subjectData?.scholarship}</td>
                <td>
                  <a href={r.subjectData?.courseLink} target="_blank" rel="noreferrer">
                    Link
                  </a>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
