import React, { useState } from "react";

const universities = [
  {
    name: "Massachusetts Institute of Technology (MIT)",
    subjects: ["Computer Science", "Mathematics", "Engineering"],
  },
  {
    name: "Stanford University",
    subjects: ["Computer Science", "Physics", "Business"],
  },
  {
    name: "University of Oxford",
    subjects: ["Law", "History", "Philosophy"],
  },
];

function App() {
  const [selectedUniversity, setSelectedUniversity] = useState("");
  const [studentType, setStudentType] = useState("International");

  const handleUniversityChange = (e) => {
    setSelectedUniversity(e.target.value);
  };

  const handleStudentTypeChange = (type) => {
    setStudentType(type);
  };

  const selectedUniData = universities.find(
    (uni) => uni.name === selectedUniversity
  );

  return (
    <div style={{ padding: "20px", maxWidth: "1000px", margin: "auto" }}>
      <h1 className="text-2xl font-bold mb-4">
        QS World University Rankings Explorer
      </h1>

      <div className="mb-4">
        <label>Select University: </label>
        <select value={selectedUniversity} onChange={handleUniversityChange}>
          <option value="">-- Select --</option>
          {universities.map((uni) => (
            <option key={uni.name} value={uni.name}>
              {uni.name}
            </option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: "20px" }}>
        <button
          style={{
            backgroundColor:
              studentType === "International" ? "green" : "#ccc",
            color: "#fff",
            padding: "8px 16px",
            marginRight: "10px",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
          onClick={() => handleStudentTypeChange("International")}
        >
          International
        </button>
        <button
          style={{
            backgroundColor: studentType === "Domestic" ? "green" : "#ccc",
            color: "#fff",
            padding: "8px 16px",
            border: "none",
            borderRadius: "4px",
            cursor: "pointer",
          }}
          onClick={() => handleStudentTypeChange("Domestic")}
        >
          Domestic
        </button>
      </div>

      {selectedUniversity && (
        <div>
          <h2>{selectedUniversity}</h2>
          <p>Student Type: {studentType}</p>
          <h3>Subjects Offered:</h3>
          <ul>
            {selectedUniData.subjects.map((subject) => (
              <li key={subject}>{subject}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
